import { useReducer } from "react";
import { createStore } from "redux";

// initial state
const initialState = {
    image: null,
    user: null,
    estate: null,
    item: null,
    show: false,
}

// reducer
const formReducer = (state = initialState, action) => {
    if (action.type === "handleCapture") {
        return {
            image: action.payload,
            user: state.user,
            estate : state.estate,
            item : state.item,
            show: state.show
        }
    } else if (action.type === "clearStateImage") {
        return {
            image: null,
            user: state.user,
            estate : state.estate,
            item : state.item,
            show: state.show
        }
    } else if (action.type === "login") {
        return {
            image: state.image,
            user: action.payload,
            estate : state.estate,
            item : state.item
        }
    } else if (action.type === "logout") {
        return {
            image: null,
            user: null,
            estate : null,
            item : null,
            show: state.show
        }
    } else if (action.type === "setEstate") {
        console.log(action.payload)
        return {
            image: state.image,
            user: state.user,
            estate : action.payload,
            item : state.item,
            show: state.show
        }
    } else if (action.type === "setItemFile") {
        console.log(action.payload)
        return {
            image: state.image,
            user: state.user,
            estate : state.estate,
            item : action.payload,
            show: state.show
        }
    } else if (action.type === "clearItemFile") {
        console.log(action.payload)
        return {
            image: state.image,
            user: state.user,
            estate : state.estate,
            item : null,
            show: state.show
        }
    } else if (action.type === "handleShow") {
        return{
            image: state.image,
            user: state.user,
            estate : state.estate,
            item: state.item,
            show: true
        }
    } else if (action.type === "handleClose") {
        return{
            image: state.image,
            user: state.user,
            estate : state.estate,
            item: state.item,
            show: false
        }
    } else {
        return {
            state,
        }
    }
};

const store = createStore(formReducer)

export default store;
