import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import { IconButton } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import PhotoCameraRoundedIcon from "@material-ui/icons/PhotoCameraRounded";
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
  root: {
    height: "100%",
    textAlign: 'center',
  },
  imgBox: {
    maxWidth: "80%",
    maxHeight: "80%",
    margin: "10px"
  },
  img: {
    height: "inherit",
    maxWidth: "inherit",
  },
  input: {
    display: "none"
  }
}));

function App() {

  const classes = useStyles();
  const [source, setSource] = useState("");
  //const [image, setImage] = useState(null);
  const dispatch = useDispatch();

  const handleCapture = (e) => {
    if (e.target.files) {
      if (e.target.files.length !== 0) {
        const file = e.target.files[0];
        const formData = new FormData();
        formData.append(
          "file",
          file
        );
        const newUrl = URL.createObjectURL(file);
        setSource(newUrl);
        //setImage(formData);
        console.log('routing to redux store')
        dispatch({type: 'handleCapture', payload: formData})
      }
    }
  };

  const storeImage = useSelector( state => state.image );

  const imageUpload = () => {
    axios.post("http://192.168.1.185:4000/images", storeImage)
    .then( res => {
      console.log( 'Axios response: ', res );
    }, (error) => {
      console.error(error);
    })
    .catch(error => console.error(error))
  }

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid item xs={12}>
          {source &&
            <Box display="flex" justifyContent="center" border={1} className={classes.imgBox}>
              <img src={source} alt={"snap"} className={classes.img}></img>
            </Box>}
          <input
            accept="image/*"
            className={classes.input}
            id="icon-button-file"
            type="file"
            capture="environment"
            onChange={(e) => handleCapture(e)}
          />
          <label htmlFor="icon-button-file">
            <IconButton
              color="primary"
              aria-label="upload picture"
              component="span"
            >
              <PhotoCameraRoundedIcon fontSize="large" color="primary" />
            </IconButton>
          </label>
        </Grid>
      </Grid>
      <button className="btn btn-primary" onClick={imageUpload}>Save file</button>
    </div>
  );
}
export default App;
