import "./style.css";
import React from "react";
import axios from "axios";
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import {
  BrowserRouter as Router,
  Route,
  useNavigate,
  Link,
  Navigate
} from "react-router-dom";

const Login = () => {
  const dispatch = useDispatch();

  const navigate = useNavigate();

  dispatch({type: "logout"});

  const loginState = (a) => {
    console.log(a);
    dispatch({type: "login", payload:a})

  };

  const estateLot = (b) => {
    console.log(b)
    dispatch({type: "setEstate", payload:b})
  };

  function onFormSubmit(event) {
    event.preventDefault()
    const form = document.getElementById("form");
    const {username,password}= form.elements;
    console.log(form.elements);
    const a = {username: username.value, password: password.value }
    console.log(username.value,password.value);

    axios.post("http://localhost:9002/ownersverify", {
        username : username.value,
        password : password.value
      })
    .then( res => {
      console.log( 'Axios response: ', res.data );
      if (res.data == "") {
        alert("INVALID CREDENTIALS");
        navigate("/")
      }
      else {
        loginState(res.data);
        axios.post("http://localhost:9002/getestate", {
        userid: res.data.id
      })
    .then( res2 => {
      console.log( 'Axios response: ', res2.data );
      if (res2.data=="") {
        navigate("/estate")
      }
      else {
        estateLot(res2.data);
        navigate("/fullmanifest")
      }
    }, (error) => {
      console.error(error);
    })
    .catch(error => console.error(error))
      }
    }, (error) => {
      console.error(error);
    })
    .catch(error => console.error(error))
  }



    return(
        <div className>
        <div className="row">
          <div className="col-lg-3"></div>
          <div className="col-lg-6">
            <div className="wrapper">
            <small className="text-danger" style={{ color: "#ffffff" }}>{ "" }</small>
              <form onSubmit={onFormSubmit} id="form"> 
                <div className="form-group">
                  <label htmlFor="">Username</label>
                  <input
                    type="text"
                    className="form-control"
                    name="username"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    name="password"
                  />
                </div>

                <input
                  type="submit"
                  value="Login"
                  className="btn btn-dark btn-block"
                />
              </form>
            </div>

          </div>
        </div>
      </div>
    )
}
export default Login;
