import { useReducer } from "react";
import { createStore } from "redux";

// initial state
const initialState = {
    image: null,
    user: null,
    estate: null,
    item: null,
}

// reducer
const formReducer = (state = initialState, action) => {
    if (action.type === "handleCapture") {
        return {
            image: action.payload,
            user: [...state.user],
            estate : [...state.estate],
            item : [...state.item]
        }
    } else if (action.type === "clearStateImage") {
        return {
            image: null,
            user: [...state.user],
            estate : [...state.estate],
            item : [...state.item]
        }
    } else if (action.type === "login") {
        return {
            image: [...state.image],
            user: [action.payload],
            estate : [...state.estate],
            item : [...state.item]
        }
    } else if (action.type === "logout") {
        return {
            image: null,
            user: null,
            estate : null,
            item : null
        }
    }else {
        return {
            state,
        }
    }
};

const store = createStore(formReducer)

export default store;
